$("#contactForm").validator().on("submit", function (event) {
    const english = window.location.href.indexOf('/en/', 0) > 0;
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        console.log(window.location.href);
        console.log();
        if (english) {
            submitMSG(false, "Please complete the missing fields");
        } else {
            submitMSG(false, "Por favor complete los campos faltantes");
        }
    } else {
        // everything looks good!
        event.preventDefault();
        submitForm(english);
    }
});


function submitForm(english){
    // Initiate Variables With Form Content
    var name = $("#name").val();
    var email = $("#email").val();
    var msg_subject = $("#msg_subject").val();
    var message = $("#message").val();


    $.ajax({
        type: "POST",
        url: "assets/php/form-process.php",
        data: "name=" + name + "&email=" + email + "&msg_subject=" + msg_subject + "&message=" + message,
        success : function(text){
            if (text == "success"){
                formSuccess(english);
            } else {
                formError();
                submitMSG(false, text);
            }
        }
    });
}

function formSuccess(english){
    $("#contactForm")[0].reset();
    if (en) {
        submitMSG(true, "The email was sent correctly!")
    } else {
        submitMSG(true, "El correo fue enviado correctamente.!")
    }
}

function formError(){
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        $(this).removeClass();
    });
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "h3 text-left tada animated text-success";
    } else {
        var msgClasses = "h3 text-left text-danger";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}